// VRFManagerV2PlusFixed.sol - 正確使用 BNB Direct Funding 模式
/**
 * @title VRFManagerV2PlusFixed
 * @notice 正確實現 Chainlink VRF V2.5 Direct Funding（使用 BNB 而非 LINK）
 * @dev 繼承 VRFV2PlusWrapperConsumerBase，使用 requestRandomnessPayInNative
 */
contract VRFManagerV2PlusFixed is VRFV2PlusWrapperConsumerBase, IVRFManager, Ownable, ReentrancyGuard {
    using Strings for uint256;
    // VRF 配置
    uint32 public callbackGasLimit = 500000;
    uint16 public requestConfirmations = 3;
    uint256 public platformFee = 0.0003 ether;
    uint256 public vrfRequestPrice = 0.005 ether; // 預估 VRF 費用
    // 存儲
    mapping(uint256 => RandomRequest) private _requests;
    mapping(address => bool) public authorizedContracts;
    mapping(address => uint256) public userToRequestId;
    mapping(address => uint256[]) public userRandomWords;
    // 事件
    event RandomRequested(uint256 indexed requestId, address indexed requester, RequestType requestType);
    event RandomFulfilled(uint256 indexed requestId, uint256[] randomWords);
    event AuthorizationUpdated(address indexed contract_, bool authorized);
    event VRFPriceUpdated(uint256 newPrice);
    event PlatformFeeUpdated(uint256 newFee);
    modifier onlyAuthorized() {
        require(authorizedContracts[msg.sender], "Not authorized");
        _;
    }
    /**
     * @dev 構造函數
     * @param _wrapperAddress BSC Mainnet V2Plus Wrapper 地址
     */
    constructor(address _wrapperAddress) 
        VRFV2PlusWrapperConsumerBase(_wrapperAddress)
        Ownable(msg.sender)
    {
        // 初始化
    }
    // ============================================
    // IVRFManager 介面實現
    // ============================================
    /**
     * @notice 獲取請求信息（實現 IVRFManager）
     */
    function requests(uint256 requestId) external view override returns (RandomRequest memory) {
        return _requests[requestId];
    }
    /**
     * @notice 獲取 VRF 請求價格（實現 IVRFManager）
     */
    function getVrfRequestPrice() external view override returns (uint256) {
        return vrfRequestPrice;
    }
    /**
     * @notice 為用戶請求隨機數（Hero/Relic 鑄造專用）
     * @dev 使用 BNB 支付而非 LINK
     */
    function requestRandomForUser(
        address user,
        uint256 quantity,
        uint8 maxRarity,
        bytes32 commitment
    ) external payable override onlyAuthorized nonReentrant returns (uint256 requestId) {
        // 計算實際 VRF 費用（使用 Native BNB）
        uint256 actualVrfPrice = i_vrfV2PlusWrapper.calculateRequestPriceNative(
            callbackGasLimit,
            uint32(quantity)
        );
        // 總費用 = VRF 費用 + 平台費
        uint256 totalFee = actualVrfPrice + platformFee;
        require(msg.value >= totalFee, "Insufficient fee");
        // 使用 BNB 請求隨機數（關鍵改動：使用 requestRandomnessPayInNative）
        // V2.5 需要正確格式化 extraArgs
        bytes memory extraArgs = abi.encodePacked(uint8(1)); // nativePayment = true
        (requestId, ) = requestRandomnessPayInNative(
            callbackGasLimit,
            requestConfirmations,
            uint32(quantity),
            extraArgs
        );
        // 存儲請求信息
        bytes memory data = abi.encode(user, quantity, maxRarity, commitment);
        _requests[requestId] = RandomRequest({
            requester: msg.sender,
            requestType: RequestType.HERO_MINT,
            data: data,
            fulfilled: false,
            randomWords: new uint256[](0)
        });
        // 存儲用戶映射
        userToRequestId[user] = requestId;
        // 退還多餘費用
        if (msg.value > totalFee) {
            payable(msg.sender).transfer(msg.value - totalFee);
        }
        emit RandomRequested(requestId, msg.sender, RequestType.HERO_MINT);
    }
    /**
     * @notice 獲取用戶的隨機數結果
     */
    function getRandomForUser(address user) external view override returns (
        bool fulfilled,
        uint256[] memory randomWords
    ) {
        uint256 requestId = userToRequestId[user];
        if (requestId == 0) {
            return (false, new uint256[](0));
        }
        RandomRequest memory request = _requests[requestId];
        return (request.fulfilled, request.randomWords);
    }
    /**
     * @notice 通用隨機數請求（實現 IVRFManager）
     * @dev 使用 BNB 支付
     */
    function requestRandomness(
        RequestType requestType,
        uint32 numWords,
        bytes calldata data
    ) external payable override onlyAuthorized nonReentrant returns (uint256 requestId) {
        // 計算實際 VRF 費用
        uint256 actualVrfPrice = i_vrfV2PlusWrapper.calculateRequestPriceNative(
            callbackGasLimit,
            numWords
        );
        // 總費用 = VRF 費用 + 平台費
        uint256 totalFee = actualVrfPrice + platformFee;
        require(msg.value >= totalFee, "Insufficient fee");
        // 使用 BNB 請求隨機數
        bytes memory extraArgs = abi.encodePacked(uint8(1)); // nativePayment = true
        (requestId, ) = requestRandomnessPayInNative(
            callbackGasLimit,
            requestConfirmations,
            numWords,
            extraArgs
        );
        // 存儲請求信息
        _requests[requestId] = RandomRequest({
            requester: msg.sender,
            requestType: requestType,
            data: data,
            fulfilled: false,
            randomWords: new uint256[](0)
        });
        // 如果是用戶相關請求，更新映射
        if (requestType == RequestType.HERO_MINT || requestType == RequestType.RELIC_MINT) {
            (address user, , , ) = abi.decode(data, (address, uint256, uint8, bytes32));
            userToRequestId[user] = requestId;
        }
        // 退還多餘費用
        if (msg.value > totalFee) {
            payable(msg.sender).transfer(msg.value - totalFee);
        }
        emit RandomRequested(requestId, msg.sender, requestType);
    }
    /**
     * @notice 授權合約（實現 IVRFManager）
     */
    function authorizeContract(address contract_) external override onlyOwner {
        authorizedContracts[contract_] = true;
        emit AuthorizationUpdated(contract_, true);
    }
    // ============================================
    // Chainlink VRF 回調
    // ============================================
    /**
     * @notice VRF Coordinator 回調函數
     * @dev 由 VRF Wrapper 調用
     */
    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        RandomRequest storage request = _requests[_requestId];
        require(!request.fulfilled, "Already fulfilled");
        request.fulfilled = true;
        request.randomWords = _randomWords;
        // 處理不同類型的請求
        if (request.requestType == RequestType.HERO_MINT || 
            request.requestType == RequestType.RELIC_MINT) {
            // 解析數據
            (address user, , , ) = abi.decode(request.data, (address, uint256, uint8, bytes32));
            // 存儲用戶的隨機數
            userRandomWords[user] = _randomWords;
        }
        // 回調請求合約
        try IVRFCallback(request.requester).onVRFFulfilled(_requestId, _randomWords) {
            // 成功
        } catch {
            // 失敗但不回滾，確保隨機數被存儲
        }
        emit RandomFulfilled(_requestId, _randomWords);
    }
    // ============================================
    // 管理函數
    // ============================================
    /**
     * @notice 設置授權合約
     */
    function setAuthorizedContract(address _contract, bool _authorized) external onlyOwner {
        authorizedContracts[_contract] = _authorized;
        emit AuthorizationUpdated(_contract, _authorized);
    }
    /**
     * @notice 設置回調 Gas 限制
     */
    function setCallbackGasLimit(uint32 _limit) external onlyOwner {
        callbackGasLimit = _limit;
    }
    /**
     * @notice 設置請求確認數
     */
    function setRequestConfirmations(uint16 _confirmations) external onlyOwner {
        requestConfirmations = _confirmations;
    }
    /**
     * @notice 設置平台費用
     */
    function setPlatformFee(uint256 _fee) external onlyOwner {
        platformFee = _fee;
        emit PlatformFeeUpdated(_fee);
    }
    /**
     * @notice 設置 VRF 請求價格
     */
    function setVrfRequestPrice(uint256 _price) external onlyOwner {
        vrfRequestPrice = _price;
        emit VRFPriceUpdated(_price);
    }
    /**
     * @notice 提取 BNB
     */
    function withdrawBNB() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No BNB to withdraw");
        payable(owner()).transfer(balance);
    }
    // ============================================
    // 輔助函數
    // ============================================
    /**
     * @notice 獲取總費用（動態計算）
     * @return 總費用（BNB）
     */
    function getTotalFee() public view returns (uint256) {
        // 估算一個典型請求的費用
        uint256 estimatedVrfPrice = i_vrfV2PlusWrapper.calculateRequestPriceNative(
            callbackGasLimit,
            1 // 估算 1 個隨機數
        );
        return estimatedVrfPrice + platformFee;
    }
    /**
     * @notice 獲取請求狀態
     */
    function getRequestStatus(uint256 requestId) external view returns (
        bool fulfilled,
        uint256[] memory randomWords
    ) {
        RandomRequest memory request = _requests[requestId];
        return (request.fulfilled, request.randomWords);
    }
    /**
     * @notice 檢查合約是否被授權
     */
    function isAuthorized(address _contract) external view returns (bool) {
        return authorizedContracts[_contract];
    }
    // Receive function
    receive() external payable {}
}
